// ──────────────────────────────────────────────────────
// TRANSFORMAR datos del formulario al formato de GHL CRM
// ──────────────────────────────────────────────────────
const body = $input.first().json.body;

// Separar nombre y apellidos
const nameParts = (body.fullName || '').trim().split(' ');
const firstName = nameParts[0] || '';
const lastName = nameParts.slice(1).join(' ') || '';

// Formatear teléfono para GHL (formato internacional +506XXXXXXXX)
let phone = (body.phone || '').replace(/\D/g, '');
if (phone.startsWith('506')) {
  phone = '+' + phone;
} else if (phone.length === 8) {
  phone = '+506' + phone;
} else {
  phone = '+' + phone;
}

// ──────────────────────────────────────────────────────
// Construir nota de historial médico para GHL
// ──────────────────────────────────────────────────────
const ts = body.timestamp
  ? new Date(body.timestamp).toLocaleString('es-CR', { timeZone: 'America/Costa_Rica' })
  : new Date().toLocaleString('es-CR', { timeZone: 'America/Costa_Rica' });

const lines = [
  '======================================',
  '   HISTORIAL MÉDICA - PRIMERA CITA    ',
  '======================================',
  '',
  '[ DATOS BÁSICOS ]',
  `Nombre completo : ${body.fullName}`,
  `Cédula / ID     : ${body.idNumber}`,
  `Edad            : ${body.age} años`,
  `Fecha nac.      : ${body.dob}`,
  `Email           : ${body.email}`,
  `Teléfono        : ${body.phone}`,
  `Fecha registro  : ${ts}`,
  '',
  '[ HISTORIAL MÉDICO ]',
  `Enfermedades crónicas : ${body.hasDisease ? 'SÍ — ' + body.diseaseDetails : 'No'}`,
  `Medicamentos actuales : ${body.takesMedication ? 'SÍ — ' + body.medicationDetails : 'No'}`,
  `Cirugías previas      : ${body.hadSurgery ? 'SÍ — ' + body.surgeryDetails : 'No'}`,
  '',
  '[ HISTORIAL MENSTRUAL ]',
  `Primera menstruación  : ${body.firstPeriodAge} años`,
  `Última menstruación   : ${body.lastPeriodDate}`,
  `Anticonceptivos       : ${body.usesContraceptive ? 'SÍ — ' + body.contraceptiveDetails : 'No'}`,
];

if (body.hasBeenPregnant) {
  lines.push('');
  lines.push('[ HISTORIAL OBSTÉTRICO ]');
  lines.push(`Total embarazos  : ${body.pregnancyCount}`);
  lines.push(`Partos vaginales : ${body.vaginalBirths || 0}`);
  lines.push(`Cesáreas         : ${body.cesareans || 0}`);
  lines.push(`Abortos          : ${body.abortions || 0}`);
  if (body.otherPregnancies) {
    lines.push(`Otros            : ${body.otherPregnancies}`);
  }
} else {
  lines.push('');
  lines.push('[ HISTORIAL OBSTÉTRICO ]');
  lines.push('Sin embarazos previos');
}

lines.push('');
lines.push('[ CITOLOGÍA / PAP SMEAR ]');
if (body.hadPapSmear) {
  lines.push('Ha tenido PAP : Sí');
  lines.push(`Último PAP    : ${body.lastPapSmear}`);
} else {
  lines.push('Ha tenido PAP : No');
}

lines.push('');
lines.push('[ ANTECEDENTES FAMILIARES ]');
lines.push(body.familyHistory || 'Sin información proporcionada');
lines.push('');
lines.push('======================================');

const medicalNote = lines.join('\n');

// ──────────────────────────────────────────────────────
// Objeto de contacto para la API de GHL (v2)
// ⚠️  Reemplaza LOCATION_ID con el ID de tu sub-cuenta GHL
// ──────────────────────────────────────────────────────
const ghlContact = {
  firstName,
  lastName,
  name: body.fullName,
  email: body.email,
  phone,
  locationId: 'REEMPLAZAR_CON_TU_GHL_LOCATION_ID',
  tags: ['nueva-paciente', 'clinica-esperanza', 'primera-cita'],
  source: 'Formulario Web - Primera Visita',
  country: 'CR',
  timezone: 'America/Costa_Rica',
};

return [{ json: { ghlContact, medicalNote, rawData: body } }];
