// ──────────────────────────────────────────────────────
// TRANSFORMAR datos del formulario al formato de GHL CRM
// ──────────────────────────────────────────────────────
const body = $input.first().json.body;

// Separar nombre y apellidos
const nameParts = (body.fullName || '').trim().split(' ');
const firstName = nameParts[0] || '';
const lastName = nameParts.slice(1).join(' ') || '';

// Formatear teléfono para GHL (formato internacional +506XXXXXXXX)
let phone = (body.phone || '').replace(/\D/g, '');
if (phone.startsWith('506')) {
  phone = '+' + phone;
} else if (phone.length === 8) {
  phone = '+506' + phone;
} else {
  phone = '+' + phone;
}

// Helper: booleano → 'Sí' / 'No'
const siNo = (val) => val ? 'Sí' : 'No';

// ──────────────────────────────────────────────────────
// Custom Fields → claves exactas de GHL
// ──────────────────────────────────────────────────────
const customFields = [

  // ── Datos Generales ──────────────────────────────────
  { key: 'contact.nmero_de_identificacin',  field_value: String(body.idNumber || '') },
  { key: 'contact.edad',                    field_value: String(body.age || '') },
  { key: 'contact.fecha_de_nacimiento',     field_value: body.dob || '' },

  // ── Antecedentes Médicos ──────────────────────────────
  { key: 'contact.padece_enfermedad',       field_value: siNo(body.hasDisease) },
  { key: 'contact.cul_enfermedad',          field_value: body.hasDisease ? (body.diseaseDetails || '') : '' },
  { key: 'contact.toma_medicamentos',       field_value: siNo(body.takesMedication) },
  { key: 'contact.cul_medicamento',         field_value: body.takesMedication ? (body.medicationDetails || '') : '' },
  { key: 'contact.ha_sido_operada',         field_value: siNo(body.hadSurgery) },
  { key: 'contact.de_qu_operacin',          field_value: body.hadSurgery ? (body.surgeryDetails || '') : '' },
  // Los siguientes no están en el formulario aún — se dejan vacíos
  { key: 'contact.hace_ejercicio',          field_value: '' },
  { key: 'contact.fuma',                    field_value: '' },

  // ── Historial Ginecológico ────────────────────────────
  { key: 'contact.edad_primera_menstruacin', field_value: String(body.firstPeriodAge || '') },
  { key: 'contact.fecha_ltima_menstruacin',  field_value: body.lastPeriodDate || '' },
  { key: 'contact.usa_anticonceptivo',       field_value: siNo(body.usesContraceptive) },
  { key: 'contact.cul_anticonceptivo',       field_value: body.usesContraceptive ? (body.contraceptiveDetails || '') : '' },
  { key: 'contact.ha_estado_embarazada',     field_value: siNo(body.hasBeenPregnant) },
  { key: 'contact.cuntas_veces_embarazada',  field_value: body.hasBeenPregnant ? String(body.pregnancyCount || '') : '' },
  { key: 'contact.partos_vaginales',         field_value: body.hasBeenPregnant ? String(body.vaginalBirths || '0') : '' },
  { key: 'contact.cesreas',                  field_value: body.hasBeenPregnant ? String(body.cesareans || '0') : '' },
  { key: 'contact.abortos',                  field_value: body.hasBeenPregnant ? String(body.abortions || '0') : '' },
  { key: 'contact.otros_embarazos',          field_value: body.hasBeenPregnant ? (body.otherPregnancies || '') : '' },

  // ── Citología y VPH ───────────────────────────────────
  { key: 'contact.se_ha_hecho_citologa',     field_value: siNo(body.hadPapSmear) },
  { key: 'contact.fecha_ltima_citologa',     field_value: body.hadPapSmear ? (body.lastPapSmear || '') : '' },
  // Los siguientes no están en el formulario aún — se dejan vacíos
  { key: 'contact.citologa_alterada',        field_value: '' },
  { key: 'contact.prueba_de_vph_realizada',  field_value: '' },

  // ── Antecedentes Familiares ───────────────────────────
  { key: 'contact.enfermedades_familiares',  field_value: body.familyHistory || '' },
];

// ──────────────────────────────────────────────────────
// Construir nota resumen para GHL (sección Notas)
// ──────────────────────────────────────────────────────
const ts = body.timestamp
  ? new Date(body.timestamp).toLocaleString('es-CR', { timeZone: 'America/Costa_Rica' })
  : new Date().toLocaleString('es-CR', { timeZone: 'America/Costa_Rica' });

const lines = [
  '======================================',
  '   HISTORIAL MÉDICA - PRIMERA CITA    ',
  '======================================',
  '',
  '[ DATOS BÁSICOS ]',
  `Nombre completo : ${body.fullName}`,
  `Cédula / ID     : ${body.idNumber}`,
  `Edad            : ${body.age} años`,
  `Fecha nac.      : ${body.dob}`,
  `Email           : ${body.email}`,
  `Teléfono        : ${body.phone}`,
  `Fecha registro  : ${ts}`,
  '',
  '[ ANTECEDENTES MÉDICOS ]',
  `Enfermedades crónicas : ${body.hasDisease ? 'SÍ — ' + body.diseaseDetails : 'No'}`,
  `Medicamentos actuales : ${body.takesMedication ? 'SÍ — ' + body.medicationDetails : 'No'}`,
  `Cirugías previas      : ${body.hadSurgery ? 'SÍ — ' + body.surgeryDetails : 'No'}`,
  '',
  '[ HISTORIAL GINECOLÓGICO ]',
  `Primera menstruación  : ${body.firstPeriodAge} años`,
  `Última menstruación   : ${body.lastPeriodDate}`,
  `Anticonceptivos       : ${body.usesContraceptive ? 'SÍ — ' + body.contraceptiveDetails : 'No'}`,
];

if (body.hasBeenPregnant) {
  lines.push('');
  lines.push('[ HISTORIAL OBSTÉTRICO ]');
  lines.push(`Total embarazos  : ${body.pregnancyCount}`);
  lines.push(`Partos vaginales : ${body.vaginalBirths || 0}`);
  lines.push(`Cesáreas         : ${body.cesareans || 0}`);
  lines.push(`Abortos          : ${body.abortions || 0}`);
  if (body.otherPregnancies) lines.push(`Otros            : ${body.otherPregnancies}`);
} else {
  lines.push('');
  lines.push('[ HISTORIAL OBSTÉTRICO ]');
  lines.push('Sin embarazos previos');
}

lines.push('');
lines.push('[ CITOLOGÍA / PAP SMEAR ]');
if (body.hadPapSmear) {
  lines.push('Ha tenido PAP : Sí');
  lines.push(`Último PAP    : ${body.lastPapSmear}`);
} else {
  lines.push('Ha tenido PAP : No');
}

lines.push('');
lines.push('[ ANTECEDENTES FAMILIARES ]');
lines.push(body.familyHistory || 'Sin información proporcionada');
lines.push('');
lines.push('======================================');

const medicalNote = lines.join('\n');

// ──────────────────────────────────────────────────────
// Objeto de contacto para la API de GHL (v2)
// ⚠️  Reemplaza LOCATION_ID con el ID de tu sub-cuenta GHL
// ──────────────────────────────────────────────────────
const ghlContact = {
  firstName,
  lastName,
  name: body.fullName,
  email: body.email,
  phone,
  locationId: 'REEMPLAZAR_CON_TU_GHL_LOCATION_ID',
  tags: ['nueva-paciente', 'clinica-esperanza', 'primera-cita'],
  source: 'Formulario Web - Primera Visita',
  country: 'CR',
  timezone: 'America/Costa_Rica',
  customFields,
};

return [{ json: { ghlContact, medicalNote, rawData: body } }];
